import React, { useState, useEffect } from 'react'
// import '../../bookface.css'

const GoogleAuth5 = ({isSignedIn, googleTest}) => {

    // var googleUser = {}

    useEffect(()=> {
        window.gapi.load('auth2', () => {
            // Retrieve the singleton for the GoogleAuth library and set up the client.
            window.gapi.auth2.init({
                client_id: '915460618193-dcl1a1f3en6f3h22evu9jqk2aqdh1lcj.apps.googleusercontent.com',
                cookiepolicy: 'single_host_origin',
                scope: 'profile'
              }).then(()=> {
                  const auth = window.gapi.auth2.getAuthInstance()
                  console.log(auth)
                  auth.attachClickHandler(document.getElementById('loginButton'));
              })
            });
        },[])

    const attachSignin = (element) => {
        console.log(element.id);
        window.gapi.attachClickHandler(element, {}, (googleUser) => {
              document.getElementById('name').innerText = "Signed in: " +
                  googleUser.getBasicProfile().getName();
            }, function(error) {
              alert(JSON.stringify(error, undefined, 2));
            });
      }


    if (isSignedIn === null){
      return(
        <h1>Checking if you are signed in...</h1>
      )
    }

    return (
        <>
        {/* <div className="googleAuthContainer">
            <div
            id="loginButton"
            >Inicia sesion con Google</div>
        </div>   */}

        <div id="gSignInWrapper">
            <span className="label">Sign in with:</span>
            <div id="loginButton" className="customGPlusSignIn">
                <span className="icon"></span>
                <span className="buttonText">Google</span>
            </div>
        </div>
        <div id="name"></div>  

        </>
    )

    // return isSignedIn ? <h1>You are logged in</h1> : <h1>You are NOT logged in</h1>

}

export default GoogleAuth5
