import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Accordion from "@material-ui/core/Accordion";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import Typography from "@material-ui/core/Typography";
// import ExpandMoreIcon from "@material-ui/icons/ExpandMore";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    height: "auto",
    borderRadius:"none",
  },
  Accordion: {
    backgroundColor: "#010606",
    color: "#ffffff",
    borderRadius:"none",
  },
  heading: {
    width:"100%",
    marginLeft:"20px",
    fontFamily: "Helvetica",
    fontSize: theme.typography.pxToRem(15),
    flexBasis: "33.33%",
    flexShrink: 0
  },

  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary
  }
}));

const QAAccordion = () => {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  const data = [
    {
      id: "panel1",
      heading: "¿Que hacemos en Sh3ck",
      secondaryHeading: "this is panel 1",
      details: "Nulla facilisi. Phasellus sollicitudin nulla et"
    },
    {
      id: "panel2",
      heading: "¿En que ciudades se encuentran disponibles?",
      secondaryHeading: "this is panel 2",
      details: " et et et et et "
    },
    {
        id: "panel3",
        heading: "¿Que hacemos en Sh3ck",
        secondaryHeading: "this is panel 1",
        details: "Nulla facilisi. Phasellus sollicitudin nulla et"
    },
      {
        id: "panel4",
        heading: "¿En que ciudades se encuentran disponibles?",
        secondaryHeading: "this is panel 2",
        details: " et et et et et "
      },
      {
        id: "panel5",
        heading: "¿Que hacemos en Sh3ck",
        secondaryHeading: "this is panel 1",
        details: "Nulla facilisi. Phasellus sollicitudin nulla et"
      },
      {
        id: "panel6",
        heading: "¿En que ciudades se encuentran disponibles?",
        secondaryHeading: "this is panel 2",
        details: " et et et et et "
      },
      {
        id: "panel7",
        heading: "¿Que hacemos en Sh3ck",
        secondaryHeading: "this is panel 1",
        details: "Nulla facilisi. Phasellus sollicitudin nulla et"
      },
      {
        id: "panel8",
        heading: "¿En que ciudades se encuentran disponibles?",
        secondaryHeading: "this is panel 2",
        details: " et et et et et "
      }
  ];

  return (
    <div className={classes.root}>
      {data.map((accordion) => {
        const { id, heading, secondaryHeading, details } = accordion;
        return (
          <Accordion
          className={classes.Accordion}
            expanded={expanded === id}
            key={id}
            onChange={handleChange(id)}
          >
            <AccordionSummary
            //   expandIcon={<ExpandMoreIcon />}
              aria-controls="panel1bh-content"
              id="panel1bh-header"
            >
              <Typography className={classes.heading}>{heading}</Typography>
              <Typography className={classes.secondaryHeading}>
                {secondaryHeading}
              </Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography>{details}</Typography>
            </AccordionDetails>
          </Accordion>
        );
      })}
    </div>
  );
}

export default QAAccordion
